import React from "react";

import "./styles/Badges.css";
import confLogo from "../images/badge-header.svg";
// import Navbar from "../components/Navbar";
import BadgesList from "../components/BadgesList";
import PageLoading from "../components/PageLoading";
import PageError from "../components/PageError";
import { Link } from "react-router-dom";

import api from "../api";
import MiniLoader from "../components/MiniLoader";

class Badges extends React.Component {
  state = {
    loading: true,
    error: null,
    data: undefined,
  };
  //   constructor(props) {
  //     super(props);
  //     console.log("1. constructor()");

  //     this.state = {
  //       data: undefined,
  //     };
  //   }

  componentDidMount() {
    this.fetchData();

    //POLLING
    this.intervalId = setInterval(this.fetchData, 5000);
  }

  componentWillUnmount() {
    clearInterval(this.intervalId);
  }

  fetchData = async () => {
    this.setState({ loading: true, error: null }); //declaramos el estado

    //llamada a la api
    try {
      const data = await api.badges.list(); //llamada al api de forma asincrona y regresa una promesa
      this.setState({ loading: false, data: data });
    } catch (error) {
      this.setState({ loading: false, error: error });
    }
  };
  //   componentDidMount() {
  //     console.log("3. componentDidMount");

  //     this.timeOutId = setTimeout(() => {
  //       this.setState({
  //         data: [
  //           {
  //             id: "2de30c42-9deb-40fc-a41f-05e62b5939a7",
  //             irstName: "Freda",
  //             lastName: "Grady",
  //             email: "Leann_Berge@gmail.com",
  //             jobTitle: "Legacy Brand Director",
  //             twitter: "FredaGrady22221-7573",
  //             avatarUrl:
  //               "https://www.gravatar.com/avatar/f63a9c45aca0e7e7de0782a6b1dff40b?d=identicon",
  //           },
  //           {
  //             id: "d00d3614-101a-44ca-b6c2-0be075aeed3d",
  //             firstName: "Major",
  //             lastName: "Rodriguez",
  //             email: "Ilene66@hotmail.com",
  //             jobTitle: "Human Research Architect",
  //             twitter: "ajorRodriguez61545",
  //             avatarUrl:
  //               "https://www.gravatar.com/avatar/d57a8be8cb9219609905da25d5f3e50a?d=identicon",
  //           },
  //           {
  //             id: "63c03386-33a2-4512-9ac1-354ad7bec5e9",
  //             firstName: "Daphney",
  //             lastName: "Torphy",
  //             email: "Ron61@hotmail.com",
  //             jobTitle: "National Markets Officer",
  //             twitter: "DaphneyTorphy96105",
  //             avatarUrl:
  //               "https://www.gravatar.com/avatar/e74e87d40e55b9ff9791c78892e55cb7?d=identicon",
  //           },
  //         ],
  //       });
  //     }, 3000);
  //   }

  //   componentDidUpdate(prevProps, prevState) {
  //     console.log("5. componentDidUpdate()");
  //     console.log({
  //       prevProps: prevProps,
  //       prevState: prevState,
  //     });

  //     console.log({
  //       props: this.props,
  //       state: this.state,
  //     });
  //   }

  //   componentWillUnmount() {
  //     console.log("6. componentWillUnmount");
  //     clearTimeout(this.timeOutId); //si el timeOut del Id esta pendiente, se desmonta de forma segura
  //   }

  render() {
    // console.log("2/4. render()");

    //si loanding es true y los datos son undefined (la primera vez que cargan) (podria ser !this.state.data)
    if (this.state.loading === true && this.state.data === undefined) {
      return <PageLoading />;
    }

    if (this.state.error) {
      return <PageError error={this.state.error} />;
    }

    return (
      <React.Fragment>
        {/* <Navbar /> */}
        {/* hero */}
        <div className="Badges">
          <div className="Badges__hero">
            <div className="Badges__container">
              <img
                className="Badges_conf-logo"
                src={confLogo}
                alt="Conf Logo"
              />
            </div>
          </div>
        </div>
        {/* hero */}

        <div className="Badges__container">
          <div className="Badges__buttons">
            <Link to="/badges/new" className="btn btn-primary">
              New Badge
            </Link>
          </div>

          <div className="Badges__list">
            <div className="Badges__container">
              <BadgesList badges={this.state.data} />

              {this.state.loading && <MiniLoader />}
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Badges;
